import VRViz from "vr-viz";

function App() {
  return (
    <VRViz
      scene={{
        sky: {
          style: {
            color: "#333",
            texture: false,
          },
        },
        lights: [
          {
            type: "directional",
            color: "#fff",
            position: "0 1 1",
            intensity: 1,
            decay: 1,
          },
          {
            type: "ambient",
            color: "#fff",
            intensity: 1,
            decay: 1,
          },
        ],
        camera: {
          position: "10 0 20",
          rotation: "0 0 0",
        },
      }}
      graph={[
        {
          type: "BarGraph",
          data: {
            dataFile: "flightTrends.csv",
            fileType: "csv",
            fieldDesc: [
              ["Year", "date", "YYYY"],
              ["Domestic", "number"],
              ["International", "number"],
              ["Total", "number"],
            ],
          },
          style: {
            dimensions: {
              width: 20,
              height: 5,
              depth: 10,
            },
          },
          mark: {
            position: {
              x: {
                field: "Year",
              },
            },
            style: {
              width: 0.2,
              depth: {
                field: "Deaths",
              },
              height: {
                field: "Tornadoes",
              },
              fill: {
                opacity: 0.4,
                scaleType: "ordinal",
                field: "Type",
                color: ["red", "green"],
              },
            },
          },
          axis: {
            "x-axis": {
              orient: "back-bottom",
              ticks: {
                noOfTicks: 10,
                size: 0.01,
                color: "white",
                opacity: 0.7,
                fontSize: 3,
              },
              grid: {
                color: "white",
                opacity: 0.7,
              },
            },
            "y-axis": {
              orient: "back-left",
              ticks: {
                noOfTicks: 10,
                size: 0.01,
                color: "white",
                opacity: 0.7,
                fontSize: 3,
              },
              grid: {
                color: "white",
                opacity: 0.7,
              },
            },
            "z-axis": {
              orient: "bottom-left",
              ticks: {
                noOfTicks: 10,
                size: 0.01,
                color: "white",
                opacity: 0.7,
                fontSize: 3,
              },
              grid: {
                color: "white",
                opacity: 0.7,
              },
            },
          },
        },
      ]}
    />
  );
}

export default App;
